// n8n Code node  ‚Äì √âtape 1 : normalisation
const legalForms = [
  'sas','sasu','sarl','eurl','sa','sca','scs','snc','sci',
  'scm','scop','sel','selarl','sep','sce','groupement'
];

const raw = $('Setup').first().json.NomEntreprise || '';

function normalize(str) {
  return str
    .toLowerCase()
    .normalize('NFD').replace(/\p{Diacritic}/gu, '')      // accents -> ASCII
    .replace(/[‚Äô'".,;:!?()]/g, ' ')                      // ponctuation -> espace
    .replace(/[^\w\s-]/g, '')                            // caract√®res sp√©ciaux
    .replace(/\s\s+/g, ' ')                              // espaces multiples -> 1
    .trim();
}

const cleaned = normalize(raw);
const formRegex = new RegExp('\\b(' + legalForms.join('|') + ')\\b', 'gi');
const coreName = cleaned.replace(formRegex, '').replace(/\s\s+/g, ' ').trim();

const parts = coreName.split(' ');
const wildStar = parts.join('*');


return {
  json: {
    original: raw,
    cleaned,           // nom complet, accents retir√©s
    coreName,          // sans forme juridique
    slug: coreName.replace(/\s+/g, '-'),
    concatNoSpace: coreName.replace(/\s+/g, ''),
    wildStar:wildStar
  }
};

